def read_data(filepath):
    names_to_labels = dict()
    with open (filepath) as infile:
        for line in infile:
            temp = line.rstrip('\r\n').split(',')
            filename = temp[0].split("/")

            expression_value = temp[6]

            if len(filename) == 2:
                names_to_labels[filename[1]] = temp[1:9]

    return names_to_labels


